import React, { Component } from 'react';
import "../styles/moviecard.css";
import 'bootstrap/dist/css/bootstrap.css';
import poster from '../poster.jpg';
import trailer from 'https://www.imdb.com/video/vi2122367257?playlistId=tt12363162&ref_=tt_ov_vi';
import {Table, Tag, Space, Image} from 'antd';
class MovieCard extends Component{
    constructor(props){
        super(props);
        this.state = {
            title: "",
            genre: "",
            releaseDate: "",
            cost: "",
            rating: "",
            user: {
                userName: "",
                rating: "",
                comment: ""
            }
        };
    }
    render(){
        return (
           <div>
               <p className="title">My People, My Homeland(2020)</p>
               <table border="1">
                   <tr>
                       <td>
                            <Image width={190} src={poster} className="poster"/>
                       </td>
                       <td>
                            123
                            <iframe style={{width:"100px",height:"180px"}}  src="https://www.youtube.com/watch?v=LaThRLnFxxw" allowfullscreen>123</iframe>
                       </td>
                   </tr>
                   <tr>
                       <td>
                            <span>Directors:</span>
                            <br />
                            <span>Actors:</span>
                            <br />
                            <span>Genre</span>
                            <br />
                            <span>Country:</span>
                            <br />
                            <span>Release date:</span>
                       </td>
                   </tr>
               </table>
           </div>
        )
    }
}

export default MovieCard;
