import React, { Component } from 'react';
import "../styles/wishList.css";
import "../styles/movie.css";
import { Layout, Button, Divider, Menu, Breadcrumb} from 'antd';
import SubMenu from 'antd/lib/menu/SubMenu';
import poster from '../poster.jpg';
import {Image} from 'antd';
import { getKeyThenIncreaseKey } from 'antd/lib/message';
const { Header, Footer, Sider, Content} = Layout;
class WishList extends Component{
    constructor(props){
        super(props);
        this.state={
            movieList:[1,2,3,4,5,6,7,8,9]
        }
    }
    handleClickByDelete(index){
        let arr = this.state.movieList;
        arr.splice(index,1);
        this.setState({
            movieList: this.state.movieList
        })
    }
    render(){
        const movies = this.state.movieList.map((k,i) => {
            <div className="movieItem" key={k}>
                <div className="movie">
                    <div className="wishmage">
                        <Image width='100%' height="100%" src={poster} className="wishPoster"/>
                    </div>
                    <Button type="primary" onClick={this.handleClickByDelete.bind(this,i)}>delete</Button>
                </div>
            </div>
        })
        return(
            <div>
                <Layout className="layout">
                    <Header>header</Header>
                    <Layout className="wishMainLayout">
                        <Sider className="wishSider">
                            <Menu theme="dark" mode='inline'>
                                <Menu.Item>menuitems</Menu.Item>
                                <SubMenu title="subMenu1">
                                    <Menu.Item>submenuitems1</Menu.Item>
                                    <Menu.Item>submenuitems2</Menu.Item>
                                    <Menu.Item>submenuitems3</Menu.Item>
                                    <Menu.Item>submenuitems4</Menu.Item>
                                </SubMenu>
                                <SubMenu title="subMenu2">
                                    <Menu.Item>submenuitems5</Menu.Item>
                                    <Menu.Item>submenuitems6</Menu.Item>
                                    <Menu.Item>submenuitems7</Menu.Item>
                                    <Menu.Item>submenuitems8</Menu.Item>
                                </SubMenu>
                            </Menu>
                        </Sider>
                        <Layout className="wishContentLayout">
                            <Breadcrumb className="bread">
                                <Breadcrumb.Item>Home</Breadcrumb.Item>
                                <Breadcrumb.Item>List</Breadcrumb.Item>
                                <Breadcrumb.Item>content</Breadcrumb.Item>
                            </Breadcrumb>
                            <Content className="wishContent">
                                <div className="movieList">
                                    {movies}
                                </div>
                            </Content>
                        </Layout>
                    </Layout>
                    <Footer>footer</Footer>
                </Layout>
            </div>
        )
    }


}

export default WishList;
