import React, { Component } from 'react';
import "../styles/moviecard.css";
import 'bootstrap/dist/css/bootstrap.css';
import poster from '../poster.jpg';
import {Table, Tag, Space, Image} from 'antd';
import YouTube from 'react-youtube';
class MovieCard extends Component{
    constructor(props){
        super(props);
        this.state = {
            title: "",
            genre: "",
            releaseDate: "",
            cost: "",
            rating: "",
            user: {
                userName: "",
                rating: "",
                comment: ""
            }
        };
    }
    render(){
        const opts={
            width: '100',
            height: '100',
            playerVars: {
                autoplay: 1,
              },
        }
        return (
           <div>
               <p className="title">My People, My Homeland(2020)</p>
               <table border="1">
                   <tr>
                       <td>
                            <Image width={190} src={poster} className="poster"/>
                       </td>
                       <td>
                            <YouTube videoId="LaThRLnFxxw" opts={opts}/>
                       </td>
                   </tr>
                   <tr>
                       <td>
                            <span>Directors:</span>
                            <br />
                            <span>Actors:</span>
                            <br />
                            <span>Genre</span>
                            <br />
                            <span>Country:</span>
                            <br />
                            <span>Release date:</span>
                       </td>
                   </tr>
               </table>
           </div>
        )
    }
}

export default MovieCard;
